import React,{useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import TextField from '@material-ui/core/TextField';


const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
        width: '5',
      },

    },
    TextField: {
        color: "white",
        fontSize: "18px",
    },
  }));

export default function Main(){
    const [selectedCharacter, setSelectedCharacter] = React.useState(0);
    const [inputValue, setInputValue] = React.useState("");
    const classes = useStyles();
    const characters = ['我','的','你','是','了'];
    const pinyin = ['wǒ','de','nǐ','shì','le'];

    const handleChange = (e) => {
        if (e.target.value !== ""){
            console.log('works')
        };
    };

    const handleKeyDown = (e) => {

        if (e.keyCode == 13){
            e.preventDefault();
            console.log('works');
            setSelectedCharacter((selectedCharacter + 1 < 5 ) ? selectedCharacter + 1 : 0);
            clear();
        }
    };

    const clear = () => {
        setInputValue("");
    }
    return(
        <div>
            <h1>
                {characters[selectedCharacter]}
            </h1>
            <form className={classes.root} noValidate autoComplete="off">
                <TextField value={inputValue} id="standard-basic"  onKeyDown={handleKeyDown} label="" inputProps={{className: classes.TextField,min: 0, style: { textAlign: 'center' }}} />
            </form>

        </div>
    );
}
